sp_angular_provider.script:
function() {
	var templete = '<div>';
	templete += '<div style="margin-top:15px; padding: 10px 15px; border-bottom: 1px solid transparent; border-color: #E6E8EA; border-top-right-radius: 3px; border-top-left-radius: 3px;">';
	templete += '<h2 style="color: #34495e; font-weight: normal; font-size: 20px; margin-top: 0; margin-bottom: 0;">';
	templete += '<i class="fa {{icon}}"></i> {{title}}';
	templete += '</h2>';
	templete += '</div>';
	templete += '<div ng-transclude>';
	templete += '</div>';
	templete += '</div>';



    return {
        restrict: 'E', 
        replace: 'true',
		transclude: true,
        template: templete,
		scope: {
			title: '@',
			icon: '@',
		}
    };
}
